	if($_POST)
	{	
		function int($int){//Funcion de entero
       
        
        if(is_numeric($int) === TRUE){ //variable logica verificar si es un numero
           
            
            if((int)$int == $int & $int >0){//Verificar si es un numero entero y si es mayor a 0
			if($int%2==0){//Se hace la condicion para mirar a ver si es divisible entre 2
				echo "<h2><u>El numero $int es par</h2></u>";//Mostrar si el numero es par
			}else{
				echo "<h2><u>El numero $int es impar</h2></u>";//Si no mostrar que es impar
			}
			}		
            
			else{
				echo "<h2><u>No es un numero entero</h2></u>";//Muestra este mensaje si no se cumple la condicion y muestra que no es un numero entero
			}
       
        
        }else{
       
            echo "<h2><u>No hay numero</h2></u>";//Si noy hay algun numero en el cuadro de texto saldra este mensaje No hay numero
        }
    }
		$Numsixk = $_POST//Recolecta lo que se puso en el campo de texto y se guarda en la variable $Numsixk
